import sys
import socket
import math as m
import hashlib
import threading
import time
import os

password = ''

def sha256(input):
    sha_signature = \
        hashlib.sha256(input.encode()).hexdigest()
    return sha_signature


def general_msg(gist, msg):
    # gist = 'checking'
    # msg = 'got'
    text = gist + '\n' + \
           msg + '\n' + \
           str(time.time()) + '\n' + \
           sha256(gist + msg + password + str(time.time()))
    return text
